package ArrayProgram;

public class SwapNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		int swap;
		System.out.println("The value of a and b is before swaping"+"  "+a+"  "+b);
		swap=a;
		a=b;
		b=swap;
		
		System.out.println("The value of a and b is after swaping"+" "+a+"  "+b);

	}

}
